sources.scss\\layouts\\_nav.scss:
@use '../abstracts' as *;

/* =============
   Start of NAVIGATION Styling
   =============
*/

.nav {
  position: absolute;
  display: flex;

  @include media-query('medium') {
    @include flex($just-cont: space-between);
    padding: 0 3vw;
    position: absolute;
    z-index: -1;
    inset: 0 0 auto 0;
    width: 100%;
    overflow: hidden;
    background-color: var(--clr-secondary-200);
    opacity: 0.95;
  }

  .logo {
    display: inline-block;
  }

  &__list {
    @include media-query('medium') {
      @include flex($just-cont: space-between);
      width: 50%;
    }
    @include media-query('large') {
      @include flex($just-cont: space-between);
      width: 40%;
    }

    &-item {
      position: absolute;
      inset: -1000% -1000% auto auto;

      @include media-query('medium') {
        inset: auto auto auto auto;
        position: relative;
      }
    }
  }

  &__menu-btn {
    $mb: &;
    @include flex;
    position: absolute;
    z-index: 1;
    inset: auto 0.25vmin 0.5vmin auto;
    width: 5em;
    height: 5em;
    cursor: pointer;
    @include transition();

    @include media-query('medium') {
      inset: auto -100% -100% auto;
    }

    &--burger {
      $mbb: &;
      @include nav-menu-brgr;
      @include transition($func: ease-in);
      @include pseudo(before, after) {
        @include nav-menu-brgr;
        @include transition($func: ease-in);
      }
      &::before {
        transform: translateY(-1em);
      }

      &::after {
        transform: translateY(1em);
      }

      @at-root #{$mb}.open {
        #{$mbb} {
          background: transparent;
          box-shadow: none;
          transform: translateX(-3.125em);
          transform: rotate(720deg);
          @include transition($func: ease-out);
        }

        $move: (0.85vh);

        #{$mbb}::before {
          transform: rotate(45deg) translate($move, -($move));
          @include transition($func: ease-out);
        }

        #{$mbb}::after {
          transform: rotate(-45deg) translate($move, $move);
          @include transition($func: ease-out);
        }
      }
    }
  }
}

